sys_script_include.script:
var RefillMedication = Class.create();
RefillMedication.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    refillmedi: function() {
        var mediId = this.getParameter("sysparm_id");
        var gr = new GlideRecord("u_medication");

        if (gr.get(mediId)) {
            var newMedi = new GlideRecord("u_medication");
            newMedi.initialize();

            newMedi.u_medication_name = gr.u_medication_name;
            newMedi.u_patient_id = gr.u_patient_id;
            newMedi.u_dosage = gr.u_dosage;
            newMedi.u_frequency = gr.u_frequency;
            newMedi.u_start_date = gr.u_start_date;
            newMedi.u_end_date = gr.u_end_date;

            newMedi.insert();
            return "true";
        }

        return "false";
    },

    type: 'RefillMedication'
});
