sys_script_include.script:
var UpdateInsuranceRecord = Class.create();
UpdateInsuranceRecord.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	updateKrdo:function()
	{
		var insuId = this.getParameter("sysparm_id");
		var status = this.getParameter("sysparm_status");
		var isactive = this.getParameter("sysparm_dactive");
		var patient = this.getParameter("sysparm_patient");
		gs.addInfoMessage("Reached here");
		var gr = new GlideRecord("u_insurance");
		gr.addQuery("sys_id", insuId);
		gr.query();
		if (gr.next()) {
			if(gr.u_status != status){
			gr.u_status = status;
			}
			if(gr.active != isactive)
			{
				gr.active = isactive;
			}
			if(gr.u_patient_id != patient)
			{
				gr.u_patient_id = patient;
			}
			gr.update();
			// gs.eventQueue("")
			return true;
		}
		return false;
	},

    type: 'UpdateInsuranceRecord'
});
