sys_script_include.script:
var DischargePatient = Class.create();
DischargePatient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    discharge: function() {
        var patId = this.getParameter("sysparm_id");
        var gr = new GlideRecord("u_patient_record");
        gr.addQuery("sys_id", patId);
        gr.query();
        if (gr.next()) {
            var roomid = gr.u_allot_room;
            var grRoom = new GlideRecord("u_room");
            grRoom.addQuery("sys_id", roomid);
            grRoom.query();
            if (grRoom.next()) {
                if (grRoom.u_occupied_beds > 0) {
                    grRoom.u_occupied_beds -= 1;
                    if (grRoom.u_occupied_beds == 0) {
                        var grWard = new GlideRecord("u_ward");
                        grWard.addQuery("sys_id", grRoom.u_ward_id);
                        grWard.query();
                        if (grWard.next()) {
                            grWard.u_occupancy -= 1;
                            grWard.update();
                        }

                    }
                    grRoom.update();
                    gr.u_allot_room = null;
					gr.update();
                }

                return true;
            }

        }
        return false;


    },
    type: 'DischargePatient'

});
