sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	var gr = new GlideRecord("u_patient_record");
	var body = email.body_text || email.body;
	var lines = body.split('\n');

	var patId = null;
	var department = null;
	var _appointment_date = null;

	for (var i = 0; i < lines.length; i++) {
		var line = lines[i].trim();

		if (line.toLowerCase().startsWith('patid')) {
			var parts = line.split(':');
			if (parts.length > 1) {
				patId = parts[1].trim();
			}
			gr.get(patId);
		}

		if (line.toLowerCase().startsWith('department')) {
			var parts = line.split(':');
			if (parts.length > 1) {
				department = parts[1].trim();
			}
		}

		if(line.toLowerCase().startsWith('appointment date'))
		{
			parts = line.split(':');
			if(parts.length > 1)
			_appointment_date = parts[1].trim();
		}
	}

	if (patId) {
		current.u_patient_id = gr.sys_id;
	}

	if (department) {
		current.u_department = department;
	}
	if(_appointment_date)
	{
		current.u_appointment_date = _appointment_date;
	}

	current.insert(); 
	logger.log("Inserted record with Patient ID: " + patId + " and Department: " + department + " sysid"+ gr.sys_id +" appointment date: "+ _appointment_date);

})(current, event, email, logger, classifier);
