sysevent_in_email_action.script:
(function runAction(current, event, email, logger, classifier) {
    var body = email.body_text || email.body || '';
    var lines = body.split('\n');

    var patId = null;
    var newPhone = null;
    var newEmail = null;

    for (var i = 0; i < lines.length; i++) {
        var line = lines[i].trim().toLowerCase();

        if (line.startsWith('patid')) {
            var parts = lines[i].split(':');
            if (parts.length > 1) {
                patId = parts[1].trim();
            }
        }

        if (line.startsWith('phone') || line.startsWith('contact')) {
            var parts = lines[i].split(':');
            if (parts.length > 1) {
                newPhone = parts[1].trim();
            }
        }

        if (line.startsWith('email')) {
            var parts = lines[i].split(':');
            if (parts.length > 1) {
                newEmail = parts[1].trim();
            }
        }
    }

    if (patId) {
        var patient = new GlideRecord('u_patient_record');
        if (patient.get(patId)) {
            if (newPhone) {
                patient.u_contact_number = newPhone;
            }
            if (newEmail) {
                patient.u_email = newEmail;
            }
            patient.update();
            logger.log("Updated contact info for patient ID: " + patId);
        } else {
            logger.log("No patient found with ID: " + patId);
        }
    } else {
        logger.log("No PatID found in the email.");
    }

})(current, event, email, logger, classifier);
